var http = require('http');

exports.createDelegatingServer = function createDelegatingServer(port, host) {
  var server = http.createServer(function(req, res) {
    delegate(req, res);
  });

  var self = {};

  self.delegateTo = function(aDelegate) {
    delegate = aDelegate;
  };
  
  server.listen(port, host); 

  return self;
}

